(ns adventofcode.day-one)

(defn check-floor-char
  [char]
  (let [eval-char char]
    (cond
      (= char \() 1 
      (= char \)) -1
      :else 0)))

(defn floor-adjustment
  [sum character]
  (+ sum
     (check-floor-char character)))

(defn calc-floor
  [input]
  (reduce floor-adjustment 0 (seq input)))

(defn indexed
  "Returns a lazy sequence of [index, item] pairs, where items come
  from 's' and indexes count up from zero.

  (indexed '(a b c d))  =>  ([0 a] [1 b] [2 c] [3 d])"
  [s]
  (map vector (iterate inc 0) s))

(defn positions
  "Returns a lazy sequence containing the positions at which pred
   is true for items in coll."
  [pred coll]
  (for [[idx elt] (indexed coll) :when (pred elt)] idx))

(defn floor-history [input]
  (reductions floor-adjustment
              0
              input))

(defn find-floor-target
  [input target-floor]
  (first (positions #{target-floor}
                    (floor-history (vec (seq input))))) )

